mapper.select:
SELECT DISTINCT SERVER .ID, HOST_NAME, IP, SSH_PORT, SSH_USER_ACCOUNT, SSH_USER_PASSWORD, SERVER . STATUS, SERVER .CREATE_BY, SERVER .CREATE_DATE, SERVER .LAST_UPDATE_BY, SERVER .LAST_UPDATE_DATE, tps.SYSTEM_ID, system.SYSTEM_NAME FROM tbl_server_info SERVER LEFT JOIN tbl_system_info system ON SERVER.SYSTEM_ID = system.ID LEFT JOIN tbl_project_system tps ON system.ID = tps.SYSTEM_ID and tps.RELATION_TYPE = 1 LEFT JOIN tbl_project_info project ON project.ID = tps.PROJECT_ID LEFT JOIN tbl_project_group groups ON groups.PROJECT_ID = project.ID LEFT JOIN tbl_project_group_user groupuser ON groupuser.PROJECT_GROUP_ID = groups.ID WHERE ( groupuser.USER_ID = #{userId} AND tps.`STATUS` = 1 AND groupuser.`STATUS` = 1 AND groups.`STATUS` = 1 AND system.`STATUS` = 1
and server.SYSTEM_ID = #{tblServerInfo.systemId}
OR `server`.SYSTEM_ID IS NULL )
and HOST_NAME like CONCAT('%',#{tblServerInfo.hostName,jdbcType=VARCHAR},'%')
and IP like CONCAT('%',#{tblServerInfo.ip,jdbcType=VARCHAR},'%')
and server.ID not in
#{item}
and server.STATUS = #{tblServerInfo.status}
and tps.SYSTEM_ID = #{tblServerInfo.systemId}
GROUP BY server.ID order by server.ID desc
