mapper.select:
  	SELECT DISTINCT sprint.ID id,sprint.SPRINT_NAME sprintName,sprint.SPRINT_START_DATE sprintStartDate,sprint.SPRINT_END_DATE sprintEndDate
	FROM tbl_sprint_info sprint
	WHERE sprint.STATUS = 1 AND sprint.SYSTEM_ID = #{systemId} and sprint.valid_STATUS = 1 and sprint.SPRINT_NAME=#{sprintName}

SELECT COUNT(*) FROM( SELECT DISTINCT sprintInfo.SPRINT_NAME AS sprintName, sprintInfo.PROJECT_ID AS projectIds, GROUP_CONCAT(sprintInfo.SYSTEM_ID) AS systemId, GROUP_CONCAT(system.SYSTEM_NAME) systemName, sprintInfo.SPRINT_START_DATE sprintStartDate, sprintInfo.SPRINT_END_DATE sprintEndDate, sprintInfo.CREATE_DATE createDate, sprintInfo.valid_STATUS AS validStatus FROM (SELECT DISTINCT sprintInfo2.* FROM tbl_sprint_info sprintInfo2 LEFT JOIN tbl_system_info systemInfo ON sprintInfo2.SYSTEM_ID = systemInfo.ID LEFT JOIN tbl_project_system ps ON ps.SYSTEM_ID = systemInfo.ID AND ps.status = 1 AND ps.RELATION_TYPE = 1 LEFT JOIN tbl_project_group pgroup ON pgroup.PROJECT_ID = ps.PROJECT_ID AND pgroup.STATUS=1 WHERE pgroup.id IN( SELECT guser2.PROJECT_GROUP_ID FROM tbl_project_group_user guser2 WHERE guser2.USER_ID=#{uid} AND guser2.STATUS=1 )) sprintInfo LEFT JOIN tbl_system_info system ON system.ID = sprintInfo.SYSTEM_ID AND system.STATUS =1
AND sprintInfo.SPRINT_NAME LIKE concat("%",#{sprintName,jdbcType=VARCHAR},"%")
AND sprintInfo.SYSTEM_ID IN
#{item}
AND system.SYSTEM_NAME LIKE concat("%",#{systemName,jdbcType=VARCHAR},"%")
AND sprintInfo.valid_STATUS = #{validStatus,jdbcType=TINYINT}
AND sprintInfo.STATUS = 1 GROUP BY sprintInfo.SPRINT_NAME,sprintInfo.PROJECT_ID,sprintInfo.SPRINT_START_DATE,sprintInfo.SPRINT_END_DATE,sprintInfo.CREATE_DATE,sprintInfo.valid_STATUS ORDER BY sprintInfo.CREATE_DATE DESC
) count

SELECT DISTINCT GROUP_CONCAT(sprintInfo.ID) AS sprintIdList, sprintInfo.SPRINT_NAME AS sprintName, sprintInfo.PROJECT_ID AS projectIds, GROUP_CONCAT(sprintInfo.SYSTEM_ID) AS systemIdList, GROUP_CONCAT(system.SYSTEM_NAME) systemName, sprintInfo.SPRINT_START_DATE sprintStartDate, sprintInfo.SPRINT_END_DATE sprintEndDate, sprintInfo.CREATE_DATE createDate, sprintInfo.valid_STATUS AS validStatus FROM (SELECT DISTINCT sprintInfo2.* FROM tbl_sprint_info sprintInfo2 LEFT JOIN tbl_system_info systemInfo ON sprintInfo2.SYSTEM_ID = systemInfo.ID LEFT JOIN tbl_project_system ps ON ps.SYSTEM_ID = systemInfo.ID AND ps.status = 1 AND ps.RELATION_TYPE = 1 LEFT JOIN tbl_project_group pgroup ON pgroup.PROJECT_ID = ps.PROJECT_ID AND pgroup.STATUS=1 WHERE pgroup.id IN( SELECT guser2.PROJECT_GROUP_ID FROM tbl_project_group_user guser2 WHERE guser2.USER_ID= #{uid} AND guser2.STATUS=1 )) sprintInfo LEFT JOIN tbl_system_info system ON system.ID = sprintInfo.SYSTEM_ID AND system.STATUS =1
AND sprintInfo.SPRINT_NAME LIKE concat("%",#{sprintName,jdbcType=VARCHAR},"%")
AND sprintInfo.SYSTEM_ID IN
#{item}
AND system.SYSTEM_NAME LIKE concat("%",#{systemName,jdbcType=VARCHAR},"%")
AND sprintInfo.valid_STATUS = #{validStatus,jdbcType=TINYINT}
AND sprintInfo.STATUS = 1 GROUP BY sprintInfo.SPRINT_NAME,sprintInfo.PROJECT_ID,sprintInfo.SPRINT_START_DATE,sprintInfo.SPRINT_END_DATE,sprintInfo.CREATE_DATE,sprintInfo.valid_STATUS ORDER BY sprintInfo.CREATE_DATE DESC
limit #{start},#{rows}

SELECT DISTINCT GROUP_CONCAT(sprintInfo.ID) AS sprintIdList, sprintInfo.SPRINT_NAME AS sprintName, sprintInfo.PROJECT_ID AS projectIds, GROUP_CONCAT(sprintInfo.SYSTEM_ID) AS systemIdList, GROUP_CONCAT(si.SYSTEM_NAME) AS systemName, sprintInfo.SPRINT_START_DATE AS sprintStartDate, sprintInfo.SPRINT_END_DATE AS sprintEndDate, sprintInfo.CREATE_DATE AS createDate, sprintInfo.valid_STATUS AS validStatus FROM tbl_sprint_info sprintInfo LEFT JOIN tbl_system_info si ON si.ID = sprintInfo.SYSTEM_ID AND si.STATUS =1
AND sprintInfo.SPRINT_NAME LIKE concat("%",#{sprintName,jdbcType=VARCHAR},"%")
AND sprintInfo.SYSTEM_ID IN
#{item}
AND si.SYSTEM_NAME LIKE concat("%",#{systemName,jdbcType=VARCHAR},"%")
AND sprintInfo.valid_STATUS = #{validStatus,jdbcType=TINYINT}
AND sprintInfo.STATUS = 1 GROUP BY sprintInfo.SPRINT_NAME,sprintInfo.PROJECT_ID,sprintInfo.SPRINT_START_DATE,sprintInfo.SPRINT_END_DATE,sprintInfo.CREATE_DATE,sprintInfo.valid_STATUS ORDER BY sprintInfo.CREATE_DATE DESC
limit #{start},#{rows}
