mapper.select:
select tsdc.system_id as systemId,tsdc.id as id ,tsdc.url as url ,tsdc.passWord as passWord, tsdc.SYSTEM_MODULE_ID systemModuleId,tsdc.ENVIRONMENT_TYPE as environmentType,tsdc.DRIVER_VERSION as driverVersion,tsdc.DRIVER_CLASS_NAME as driverClassName ,tsdc.USER_NAME as userName, tsi.SYSTEM_NAME as systemName,tsmi.MODULE_NAME as moduleName
from tbl_system_db_config tsdc left join tbl_system_info tsi on tsi.id=tsdc.system_id left join tbl_system_module tsmi on tsmi.id=tsdc.system_module_id
and tsdc.status=1
and tsdc.system_id=#{systemId}
and tsdc.system_module_id=#{systemModuleId}
and tsdc.ENVIRONMENT_TYPE=#{environmentType}
order by tsdc.system_id

SELECT DISTINCT tsdc.system_id AS systemId, tsdc.id AS id, tsdc.url AS url, tsdc. PASSWORD AS PASSWORD, tsdc.SYSTEM_MODULE_ID systemModuleId, tsdc.ENVIRONMENT_TYPE AS environmentType, tsdc.DRIVER_VERSION AS driverVersion, tsdc.DRIVER_CLASS_NAME AS driverClassName, tsdc.USER_NAME AS userName, tsi.SYSTEM_NAME AS systemName, tsmi.MODULE_NAME AS moduleName FROM tbl_system_db_config tsdc LEFT JOIN tbl_system_info tsi ON tsi.id = tsdc.system_id LEFT JOIN tbl_system_module tsmi ON tsmi.id = tsdc.system_module_id LEFT JOIN tbl_project_system tps ON tsi.ID = tps.SYSTEM_ID and tps.RELATION_TYPE = 1 LEFT JOIN tbl_project_info project ON project.ID = tps.PROJECT_ID LEFT JOIN tbl_project_group groups ON groups.PROJECT_ID = project.ID LEFT JOIN tbl_project_group_user groupuser ON groupuser.PROJECT_GROUP_ID = groups.ID WHERE groupuser.USER_ID = 1 AND groupuser. STATUS = 1 AND tps.`STATUS` = 1 AND groups. STATUS = 1 AND tsi. STATUS = 1 AND tsdc. STATUS = 1
and tsdc.system_id=#{systemId}
and tsdc.system_module_id=#{systemModuleId}
and tsdc.ENVIRONMENT_TYPE=#{environmentType}
order by tsdc.system_id
