SELECT tjrs.build_STATUS buildStatus,
tjrs.END_DATE endDate, tjrs.START_DATE startDate, tjrs.ID id, tjrs.job_name jobName, tjrs.CREATE_TYPE createType, tj.status,
tjrs.ENVIRONMENT_TYPE envType, tui.USER_NAME userName, tjrs.BUILD_PARAMETER buildParameter FROM tbl_system_jenkins_job_run tjrs LEFT JOIN tbl_system_jenkins tj ON tjrs.system_jenkins_ID = tj.ID LEFT JOIN tbl_system_scm tss ON tss.ID = tj.SYSTEM_SCM_ID LEFT JOIN tbl_user_info tui on tui.id=tjrs.CREATE_BY where tjrs.SYSTEM_ID = #{systemId,jdbcType=BIGINT} and tjrs.CREATE_TYPE=#{createType,jdbcType=TINYINT} and tjrs.job_type=#{jobType,jdbcType=TINYINT}
and( tjrs.build_STATUS=3 or tjrs.build_STATUS=2 or tjrs.build_STATUS=5) and tjrs.status=1
and tjrs.system_jenkins_ID= #{systemJenkinsId,jdbcType=BIGINT}
and tjrs.ENVIRONMENT_TYPE= #{envType,jdbcType=TINYINT}
order by tjrs.END_DATE DESC limit #{start},#{pageSize}

