mapper.update:
update tbl_system_module_jenkins_job_run
set


       status = #{status,jdbcType=TINYINT},


  LAST_UPDATE_DATE = #{lastUpdateDate,jdbcType=TIMESTAMP}
where SYSTEM_JENKINS_JOB_RUN = #{systemJenkinsJobRun,jdbcType=BIGINT}

mapper.select:
SELECT tsmjr.CREATE_DATE createTime,SUM(tsmjr.SONAR_BUGS) SONAR_BUGS,SUM(tsmjr.SONAR_CODE_SMELLS) SONAR_CODE_SMELLS, SUM(tsmjr.SONAR_VULNERABILITIES) SONAR_VULNERABILITIES,SUM(tsmjr.SONAR_COVERAGE) SONAR_COVERAGE, SUM(tsmjr.SONAR_DUPLICATIONS) SONAR_DUPLICATIONS,SUM(tsmjr.SONAR_UNIT_TEST_NUMBER) SONAR_UNIT_TEST_NUMBER FROM tbl_system_module_jenkins_job_run tsmjr WHERE tsmjr.STATUS = 1 AND tsmjr.JOB_TYPE = 1 AND tsmjr.SONAR_BUGS IS NOT NULL
AND tsmjr.SYSTEM_ID = #{systemId}
AND tsmjr.SYSTEM_MODULE_ID=#{systemModuleId}
GROUP BY tsmjr.CREATE_DATE ORDER BY tsmjr.CREATE_DATE DESC LIMIT 0,1

SELECT tsmjr.CREATE_DATE createDate,IFNULL(SUM(tsmjr.SONAR_BUGS),0) bugsCount,IFNULL(SUM(tsmjr.SONAR_CODE_SMELLS),0) smellsCount,IFNULL(SUM(tsmjr.SONAR_VULNERABILITIES),0) vulnerabilitiesCount,IFNULL(SUM(tsmjr.SONAR_COVERAGE),0) coverageCount,IFNULL(SUM(tsmjr.SONAR_DUPLICATIONS),0) duplicationsCount,IFNULL(SUM(tsmjr.SONAR_UNIT_TEST_NUMBER),0) sonarUnitTestNumber FROM tbl_system_module_jenkins_job_run tsmjr WHERE tsmjr.STATUS = 1 AND tsmjr.JOB_TYPE = 1 AND tsmjr.SONAR_BUGS IS NOT NULL
AND tsmjr.SYSTEM_ID = #{systemId}
AND tsmjr.SYSTEM_MODULE_ID=#{systemModuleId}
GROUP BY tsmjr.CREATE_DATE ORDER BY tsmjr.CREATE_DATE DESC LIMIT 0,7
