mapper.update:
UPDATE tbl_system_module_scm
set STATUS = 2
where  SYSTEM_SCM_ID = #{id} and SYSTEM_ID = #{systemId}

  	update tbl_system_module_scm
	set STATUS = 2
	WHERE SYSTEM_MODULE_ID = #{systemModuleId}

