  #{sonarProjectKey,jdbcType=VARCHAR}, #{sonarProjectName,jdbcType=VARCHAR}, #{sonarSources,jdbcType=VARCHAR}, 
  #{sonarJavaBinaries,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, #{createBy,jdbcType=BIGINT}, 
