mapper.select:
      SELECT COUNT(*) AS riskInfoCount
		FROM tbl_risk_info
		WHERE STATUS = 1 AND PROGRAM_ID = #{programId}

    SELECT COUNT(*) AS riskWeekAdd
		FROM tbl_risk_info
		WHERE YEARWEEK(DATE_FORMAT(CREATE_DATE,'%Y-%m-%d')) = YEARWEEK(NOW()) AND STATUS = 1 AND PROGRAM_ID = #{programId}

     	SELECT COUNT(*) AS outStandingNumber
		FROM tbl_risk_info
		WHERE  STATUS = 1 AND RISK_STATUS IN(1,3) AND PROGRAM_ID = #{programId}

    SELECT COUNT(*) AS changeInfoCount
		FROM tbl_project_change_info
		WHERE STATUS = 1 AND PROGRAM_ID = #{programId}

    SELECT COUNT(*) AS changeWeekAdd
		FROM tbl_project_change_info
		WHERE YEARWEEK(DATE_FORMAT(CREATE_DATE,'%Y-%m-%d')) = YEARWEEK(NOW()) AND STATUS = 1 AND PROGRAM_ID = #{programId}

     SELECT COUNT(*) AS confirmationNumber
	  FROM tbl_project_change_info
	  WHERE  STATUS = 1 AND CHANGE_STATUS NOT IN(5,6) AND PROGRAM_ID = #{programId}

SELECT spr.PROJECT_ID AS projectId,sum(rfh.ESTIMATE_REMAIN_WORKLOAD) AS estimateRemainWorkload,rfh.CREATE_DATE AS createDate FROM tbl_sprint_info spr LEFT JOIN tbl_requirement_feature req ON spr.ID = req.SPRINT_ID LEFT JOIN ( SELECT DISTINCT REQUIREMENT_FEATURE_ID,ESTIMATE_REMAIN_WORKLOAD,CREATE_DATE from tbl_requirement_feature_history
SPRINT_ID IN #{item}
) rfh ON rfh.REQUIREMENT_FEATURE_ID = req.ID WHERE spr.PROJECT_ID IN ( select DISTINCT pp.PROJECT_ID from tbl_program_project pp LEFT JOIN tbl_project_info pro ON pp.PROJECT_ID = pro.ID where pp.PROGRAM_ID = #{programId} AND pp.STATUS = 1 AND pro.DEVELOPMENT_MODE = 1 AND pro.STATUS = 1 ) AND req.STATUS = 1 AND spr.STATUS =1 AND #{currentDate} >= spr.SPRINT_START_DATE AND #{currentDate} <= spr.SPRINT_END_DATE AND rfh.CREATE_DATE is not null GROUP BY rfh.CREATE_DATE,spr.PROJECT_ID

SELECT MESSAGE_TITLE AS messageTitle, MESSAGE_CONTENT AS messageContent, CREATE_DATE AS createDate FROM tbl_message_info
PROJECT_ID IN #{item}
ORDER BY CREATE_DATE DESC limit 8
