SPRINT_ID IN #{item}
) rfh ON rfh.REQUIREMENT_FEATURE_ID = req.ID WHERE spr.PROJECT_ID = #{projectId} AND req.STATUS = 1 AND spr.STATUS =1 AND #{currentDate} >= spr.SPRINT_START_DATE AND #{currentDate} <= spr.SPRINT_END_DATE AND rfh.ESTIMATE_REMAIN_WORKLOAD is not null AND DATE_FORMAT(rfh.CREATE_DATE,'%Y-%m-%d') is not null GROUP BY DATE_FORMAT(rfh.CREATE_DATE,'%Y-%m-%d')

