mapper.select:
SELECT DISTINCT TUI.ID, TUI.USER_NAME, TUI.USER_ACCOUNT, TUI.USER_SCM_ACCOUNT, TUI.EMPLOYEE_NUMBER, TUI.USER_SCM_PASSWORD, TUI.EMAIL, TDE.DEPT_NAME, TCO.COMPANY_NAME,
GROUP_CONCAT(DISTINCT TPG.PROJECT_GROUP_NAME) AS "MY_PROJECT_GROUP_NAME" FROM TBL_USER_INFO TUI LEFT JOIN TBL_DEPT_INFO TDE ON TDE.ID = TUI.DEPT_ID AND TDE.STATUS = 1 LEFT JOIN TBL_COMPANY_INFO TCO ON TCO.ID =TUI.COMPANY_ID AND TCO.STATUS = 1 LEFT JOIN TBL_PROJECT_GROUP_USER TPGU ON TPGU.USER_ID = TUI.ID AND TPGU.STATUS = 1 LEFT JOIN TBL_PROJECT_GROUP TPG ON TPG.ID = TPGU.PROJECT_GROUP_ID AND TPG.STATUS = 1
TUI.STATUS = 1
AND TUI.USER_NAME LIKE CONCAT(CONCAT('%',#{userName},'%'))
AND LOWER(TUI.EMPLOYEE_NUMBER) LIKE CONCAT(CONCAT('%',LOWER(#{employeeNumber}),'%'))
AND TDE.ID = #{deptId}
AND TPG.PROJECT_ID = #{projectId}
AND TPG.ID IN
#{item1}
AND TPGU.USER_POST = #{userPost}
AND TUI.ID NOT IN
#{item1}
GROUP BY TUI.ID
ORDER BY FIELD(TUI.ID,
#{item2}
) DESC

SELECT DISTINCT tui.ID, TUI.USER_NAME, TDE.DEPT_NAME, TCO.COMPANY_NAME FROM tbl_user_info tui
LEFT JOIN tbl_project_group_user tpgu ON tpgu.USER_ID = tui.ID LEFT JOIN tbl_project_group tpg ON tpg.ID = tpgu.PROJECT_GROUP_ID LEFT JOIN tbl_project_info tpi ON tpi.ID = tpg.PROJECT_ID
LEFT JOIN TBL_DEPT_INFO TDE ON TDE.ID = TUI.DEPT_ID AND TDE.STATUS = 1 LEFT JOIN TBL_COMPANY_INFO TCO ON TCO.ID =TUI.COMPANY_ID AND TCO.STATUS = 1
TUI.STATUS = 1
AND tpi.ID IN ( SELECT DISTINCT tpi1.ID FROM tbl_project_group_user tpgu1 LEFT JOIN tbl_project_group tpg1 ON tpg1.ID = tpgu1.PROJECT_GROUP_ID LEFT JOIN tbl_project_info tpi1 ON tpi1.ID = tpg1.PROJECT_ID WHERE tpgu1.USER_ID = #{currentUserId} AND tpg1.status = 1 AND tpgu1.STATUS = 1 AND tpi1.STATUS = 1 )
AND tui.USER_NAME LIKE CONCAT(CONCAT('%',#{tblUserInfoDTO.userName},'%'))
AND TDE.ID = #{tblUserInfoDTO.deptId}
AND TCO.ID = #{tblUserInfoDTO.companyId}
AND TUI.ID NOT IN
#{item}

SELECT DISTINCT userinfo.id as USER_ID,userinfo.USER_NAME,company.COMPANY_NAME,dept.DEPT_NAME FROM tbl_user_info userinfo LEFT JOIN tbl_project_group_user groupuser ON userinfo.ID = groupuser.USER_ID AND groupuser.`STATUS`=1 LEFT JOIN tbl_project_group projectgroup ON groupuser.PROJECT_GROUP_ID = projectgroup.ID AND projectgroup.`STATUS`=1 LEFT JOIN tbl_project_info project ON projectgroup.PROJECT_ID = project.ID LEFT JOIN tbl_system_info system ON project.ID = system.PROJECT_ID LEFT JOIN tbl_requirement_feature requirement ON system.ID = requirement.SYSTEM_ID LEFT JOIN tbl_dev_task dev ON requirement.ID = dev.REQUIREMENT_FEATURE_ID LEFT JOIN tbl_dept_info dept on userinfo.DEPT_ID=dept.id AND dept.`STATUS`=1 LEFT JOIN tbl_company_info company on userinfo.COMPANY_ID=company.id AND company.`STATUS`=1
userinfo.STATUS=1
and dev.ID =#{devID}
AND userinfo.ID!=#{notWithUserID}
AND dept.DEPT_NAME LIKE CONCAT('%',#{deptName},'%')
AND company.COMPANY_NAME LIKE CONCAT('%',#{companyName},'%')
AND userinfo.USER_NAME LIKE CONCAT('%',#{userName},'%')
ORDER BY userinfo.id asc
limit #{start},#{pageSize}

SELECT userInfo.id as USER_ID,userInfo.USER_NAME,company.COMPANY_NAME,dept.DEPT_NAME FROM
tbl_user_info userInfo
LEFT JOIN tbl_dept_info dept
on userInfo.DEPT_ID=dept.id
LEFT JOIN tbl_company_info company
on userInfo.COMPANY_ID=company.id

    userInfo.ID in(SELECT guser.USER_ID from tbl_project_group_user guser where guser.PROJECT_GROUP_ID=#{id})
    and userInfo.STATUS=1

        and dev.ID =#{devID}


        AND userInfo.ID!=#{notWithUserID}


        AND userinfo.DEPT_ID = #{tblUserInfo.deptId}


        AND userinfo.company_id = #{tblUserInfo.companyId}


        AND userInfo.USER_NAME LIKE CONCAT(CONCAT('%',#{tblUserInfo.userName},'%'))


ORDER BY userInfo.id asc

    limit #{start},#{pageSize}

SELECT userInfo.id as USER_ID,userInfo.USER_NAME,userInfo.USER_ACCOUNT,company.COMPANY_NAME,dept.DEPT_NAME,userInfo.STATUS FROM tbl_user_info userInfo LEFT JOIN tbl_dept_info dept on userinfo.DEPT_ID=dept.id and dept.STATUS=1 LEFT JOIN tbl_company_info company on userinfo.COMPANY_ID=company.id and company.STATUS=1
userInfo.ID IN( SELECT DISTINCT pgUser.USER_ID FROM tbl_project_group_user pgUser WHERE pgUser.PROJECT_GROUP_ID in( SELECT DISTINCT PgroupID.ID from tbl_project_group PgroupID WHERE PgroupID.PROJECT_ID IN( SELECT DISTINCT pgroup.PROJECT_ID FROM tbl_project_group pgroup LEFT JOIN tbl_project_group_user guser2 ON pgroup.ID =guser2.PROJECT_GROUP_ID WHERE guser2.USER_ID=#{id} AND pgroup.STATUS=1 AND guser2.STATUS=1 ))) and userInfo.STATUS=1
AND userInfo.ID!=#{notWithUserID}
AND dept.DEPT_NAME LIKE CONCAT('%',#{deptName},'%')
AND company.COMPANY_NAME LIKE CONCAT('%',#{companyName},'%')
AND userInfo.USER_NAME LIKE CONCAT('%',#{userName},'%')
AND userInfo.USER_STATUS =#{userStatus}
ORDER BY userInfo.id asc
limit #{start},#{pageSize}

