INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3633', '核心业务系统-车险', '次要错误', '2014-02-24', '车险核保工作流系统调用投保单查询页面点击“投保查询信息”报错Array index out of range: 0', '车辆险部2013014901', '该bug是因为大版本需求“核心_关于解除出单员和查询码绑定限制的需求”升级导致的，由于该需求是针对核心业务系统的修改，未考虑到影响核保作业流系统保单信息查看功能', '核心系统功能修改，未考虑到对周边系统的影响', '对核心系统的修改，测试人员需要多考虑对周边系统的影响。\n', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3646', '核心业务系统-非车', '严重错误', '2014-03-24', '网销家财险保险卡无法激活和查询', '非车险部2013007201', '四川保险卡需求上线改造了保险卡网站激活接口，使用了接口定义中的扩展信息列表属性（expandList）；程序改造中为防止用户使用意外险保险卡从网销家财险入口激活，在扩展属性数组中扩增了一个属性ProductType使用；后台程序直接获取扩展信息列表属性使用，而网销的家财险前期开发时并未传送expandList节点，所以本次需求上线后家财险保险卡激活会报错。', '非机动车保险卡业务需求的测试任务，仅测试了意外险的保险卡却忽略了其它保险卡业务的测试', '1、测试人员需着手完善接口服务类的测试用例模板，针对接口定义中允许为空的属性，补充删除节点的测试用例；\n2、测试组在接收到服务类改造需求测试时，要确认清楚接口所影响的所有应用系统，和业务场景。对新增接口的每个节点（含父节点和子节点）所涉及的业务场景都要测试到位；测试用例要覆盖全面。\n3、不能把思路局限在开发人员所列系统影响范围，要积累经验，从第三方角度评估影响范围。\n4、该生产缺陷周末分两次紧急发布才修复该缺陷，期间未通知测试人员到场测试，存在很大风险。建议开发人员对紧急升级补丁时，也要通过第三方测试才能发布。\n', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3679', '人身险改造项目', '严重错误', '2014-04-28', '部分渠道对接无法出单', '金融渠道部2013000201', '该问题是新增功能影响了原有功能，需求分析不全面：\n二期渠道移植需求，在通用校验中增加了保额、保费不能小于等于0的校验。而部分原有渠道，传过来的总保额为0。', '需求跨度长，测试时间不足，测试人员未意识到新渠道的新规则对老渠道有影响，在对老渠道流程进行回归测试时未对具体规则进行校验。', '1、对各渠道对接规则进行整理，将散落在各处的所有渠道规则整理至一份文档中，并及时更新，希望测试组抽空进行。\n2、通用对接规则发生变化时，需对老渠道进行影响分析后对可能的影响点进行针对性回归测试。\n3、以上2项工作都需要开发人员的配合。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3682', '电销系统', '严重错误', '2014-04-29', '配送交接清单出现问题。配送系统，客户自取和要求配送的客户一起显示。并且导出表格以后还在。', '电子商务中心2014004301', '该问题是新增功能影响了原有功能，需求分析不全面：\n客户自取件不应显示的问题在需求中未提及，属于需求问题；\n导出筛选过滤功能也未在需求中提及，但在查询界面中相关按钮一直存在，在需求人员未特意说明的情况下，开发、测试人员也未注意到。', '未对配送查询界面中的导出筛选过滤按钮进行测试。', '1、系统老功能界面发生变更时，测试应尽量对变更界面的所有功能按钮进行回归测试。\n2、将配送清单查询模块的导出筛选过滤功能加入测试关注要点。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3721', '应收应付系统', '严重错误', '2014-06-06', '多笔挂账单笔核销，凭证科目生成错误', 'QC336', '开发的过程，一个关键词使用错误，导致应该继续的循环查询的地方直接退出；该问题对于2条以下的没有问题，对于多笔以上的有问题；\n另外就是对业务场景的考虑不够全面，税费类核销一般都是几十笔一块核销，业务场景把握不够准确；\n后续建议：\n1. 加强代码走查，自测力度；\n2. 加强应收应付的实际业务场景的需求，脱离了业务的开发会出现问题；\n', '测试多笔时只测试了2笔数据发现问题，开发解决后只验证了2笔问题解决，未测试2笔以上', '测试组以后测试多笔挂帐都测试3笔以上的数据', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3725', '核心业务系统-非车', '严重错误 ', '2014-06-10', '湖南卡单方案维护提示错误信息：保存失败', '财产险部_农险部2014004201 ', '人身险的方案维护功能取flag[10]为0的最大方案代码+1作为新的方案代码，但我们6月6号上线的LJT方案改造需求代码变更中ccic.prpdformula.flag置为空，导致人身险的方案代码报主键冲突。', '在测试时，未关注基础表关键字段的配置是否正确。', '在以后的测试过程中，针对基础数据配置表有变更的需求，测试时不能忽视变更后配置表的各个字段的含义，可参照类似代码变更的处理。\n如果存在关键字段为空时，应向开发确认，这个字段是否涉及程序的判断。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3785', '理赔工作流系统', '次要错误', '2014-08-27', '双代案件分公司超核价权限的案件没有提交总公司按钮', '理赔管理部2014003601', '双代案件若撤回承保地做定损任务，并且其中定损金额超过当前操作员的核价金额权限，需要提交到总公司进行核价、人伤核损。但现在分公司核价人员进入核价页面后，没有提交总公司核价按钮，因程序判断是否是双代案件将定核损主表的veripcompanyGrade的值设置为1，实际应该为2。', '非双代案件，核价金额大于分公司人员权限的情况未考虑到，属于页面场景未考虑全面', '1、后续类似需求，要考虑核价金额或核损金额超过操作员权限的情况\n2、由于目前手机端也可以定损，也要考虑到手机端对于双代案件提交定损后，理赔后续的处理流程验证。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3817', 'Ecargo系统', '次要错误', '经常', 'eCargo业务来源2的校验规则有误', '历史问题', 'QC940任务修复时产生的新问题,QC940修复的是业务来源2不能为空,新问题是业务来源1选择2401时,且又是重客的时候带出的业务来源2的值在核心校验不通过,核心对这个2401的又是重客的有个控制。所以现在开发在系统内将这个校验给去掉了。', '未考虑到特殊情况,只有在选择业务来源1为2401,核心会对业务来源2和业务代码等进行。', '特殊控制的情况较多，较隐蔽，测试很难全部考虑到，已对此类特殊情况情况进行梳理，更新用例库，避免下次漏检。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3847', '报价平台', '次要错误', '有时', '包含商业险业务续单交保单时客户忠诚度优惠错误', '车辆险部20140143', '目前核心的续保匹配规则不区分险别，上年单交，本年保了商业险，核心续保服务也会匹配到被续保保单，ILOG根据报价送的投保状态为续保，返回客户忠诚度可用优惠。但是上年单交，本年投保商业险时平台不会给客户忠诚度优惠。\n', '1.回归BUG5626时未跑完整流程\n2.未充分考虑到单交续保商业险的情况', '将上年单保交强险，今年保商业险的案例加入到测试用例库中。测试时充分考虑特殊情况。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3848', 'Ecargo系统', '严重错误', '经常', '分公司反映使用应用模板时报错及总公司审核协议批单时报错', '财产险部_农险部2014001703', '因反洗钱需求中没有考虑到历史协议批改后将批单转换为生效协议时校验反洗钱信息非空，导致对象转换空指针异常；模版使用同样是未考虑到历史模版对象转换反洗钱信息非空出现空指针异常', '测试时协议少，测试人员一般直接录入新协议，很少使用模板，生产上业务会经常使用模板来快速录入协议。', '将应用模板这条用例添加到验证测试用例里，作为基础业务用例执行。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3880', '核心业务系统-车险', '严重错误', '经常', '核心业务系统无法打印DDB保单', '车商渠道部2014003301', 'ECAR增加“车加随车行李险”功能的需求，对保单打印功能调整。程序改动影响到DDB摩托车农用拖拉机定额保险的保单打印，导致无法出单。', '该需求修改车险保单打印功能，测试时仅测试了车险保单打印，未考虑摩托车保单打印情况。', '即使仅修改了车险保单打印，在时间允许的情况下也尽量验证多种单证的打印。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3904', '核心业务系统-非车', '次要错误', '经常', '雇主责任险普通批改丢失雇员清单数据', '财产险部_农险部2014012702', '产生原因：该问题是“财产险部_农险部2014012601-关于对监护人责任保险录单界面及保险凭证打印进行修改的需求”等一系列清单导入的需求在2014-11-21号上线遗留的历史问题，\n批单核批通过时针对页面普通批改未批改雇员信息的，会prpcname表数据；问题2，ZFG雇员清单身份证校验规则控制不合理，导致历史数据无法进行批改；', '原始需求测试中已发现19个缺陷，测试时间紧迫，测试侧重点放到了清单导入，没有考虑批改雇员清单。', '测试的时候考虑要全面，对功能修改的影响范围不确定时，要和开发沟通，共同分析改动点对系统的影响。对于监护人责任保险，除了新保导入清单后，也应该操作批改。在发现较多缺陷的时候，应该重新评估开发质量，增加测试用例覆盖。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3916', '收付费及发票打印', '次要错误', '有时', '电网销系统中无法获取联合销售（车+人）保单核保通知的问题', '电网销系统中无法获取联合销售（车+人）保单核保通知的问题', '收付费升级了车商渠道部2014003309-ECAR增加“车加人”功能需求－收付需求，该需求主要是对于前端1+多的单子进行收付费支持的调整，但是在调整的过程中，对于原来的1+1联合销售的情况考虑不周，影响到了核保通知的接口。', '在收付费中处理的默认都是核保通过以后的单子，所以就忽略了该部分的流程。\n', '对于联合销售的业务，1对N、1对1的用例都要在测试范围之内。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3925', '核心业务系统-车险', '次要错误', '经常', 'DDK保单查看无法显示险别信息', '车辆险部2015001101', '上海新增首年投保日期，分为核心和报价两个需求，核心部分需要测试DDH、DDK两个险种的查询页面，包括投保查询、保单查询和批单查询，以及其他非上海地区的查询页面，需求测试时遗漏了查询页面，大版本流程也没有覆盖到，导致此问题的产生。', '测试人员只关注了承保，遗漏了查询页面的检查。大版本流程测试过于表面化，测试时没有关注上线的需求，只是简单的跑一下流程，也导致此问题漏检。', '对于增加字段的需求，无论核心需求还是报价需求，均需要测试投保查询页面、保单查询页面、批单查询页面，需要覆盖DDH DDK甚至DDM DDB DDE，需要覆盖需求相关地区以及其他地区\n', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('QCBUG 15455', '核心业务系统-车险', '次要错误', '经常', 'ILOG新增的入参“上年赔付率”没有传值，“车辆类型”传值有误', '商车改革', '1.上年赔付率没有传值，赔付率=赔付金额/上年保费，使用了无值的“上年保费。\n2.车辆类型传值有误，产生原因分析\n', '测试时由于测试数据不充分等原因，对ILOG新增入参测试不充分，导致一些情况没有覆盖全面，传送给ILOG的车辆类型，测试时由于不太清楚ILOG复杂的解析规则，导致在测试阶段车辆类型覆盖不全面', '今后在测试类似需求时应该充分考虑因子在不同情况下的传值。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3943', '核心业务系统-车险', '次要错误', '经常', '北京电子保单上线后批单打印失败', '车辆险部2014016801', '北京电子保单上线后影响到正常批单打印。系统提示投保确认码和批单确认码不能同时为空，无法正常打印。', '当周版测环境数据库升级，版本环境基本不能使用，导致版本测试时间非常紧张，反向用例数量被压缩。', '电子保单上线除电子保单本身以外，务必测试常规保单的打印。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3953', '报价平台', '次要错误', '2015-08-22', 'DDG续保录入DDK保单时，投保查询无法操作', '无', '在做投单复制时对车队标志做了重新赋值，在程序处理时将DDK排除在外了，导致此问题。测试时忽略了续保业务的测试', '投保单快速录入需求，导致DDK在DDG中续保时查询失败，对于查询页面改动的需求，入口仅考虑了新保的情况。', '在测试类似需求时，不能只测试新保保单，注意补充续保测试场景。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3968', '核心业务系统-车险', '次要错误', '2015-10-26', '费改地区-无法支持60-特种车四的出单', '无', '问题1：平台新增加的车辆种类“61-特种四挂车”，对原有“60-特种车四”有影响，PICS与核心系统在进行解析时规则不一致，导致原有车辆种类“60-特种车四”在投保查询和投保预确认时发送不一致；\n问题2：与ECIF交互的方式由异步改为同步，程序中代码的执行顺序发生变化，在开发自测试过程中未关注异步交互方式后的代码对业务的影响；\n问题3：对于支付方式程序中需要特殊处理的代码，移动POS程序中缺少处理。', '问题1：平台新增加的车辆种类“61-特种四挂车”，对原有“60-特种车四”有影响，PICS与核心系统在进行解析时规则不一致，导致原有车辆种类“60-特种车四”在投保查询和投保预确认时发送不一致，由于本次需求主要测“61-特种四挂车”，而对于“60-特种车四”的业务流程以流程测试为主，但测试人员只测到了投保生成，未测试后续生成保单流程，导致漏检。\n问题2：与ECIF交互的方式由异步改为同步，程序中代码的执行顺序发生变化，在测试过程中未关注异步交互方式后的业务流程\n问题3：打印的测试从未出现过因缴费方式不同而引起的问题，由于惯性思维导致未考虑到这样的业务场景', '1.涉及投保的需求，不能仅测试投保，要把后续的相关流程都测全面，务必生成保单。 \n2.对于系统间交互，对同步异步交互方式都要测试。 \n3.每一种业务场景流程都需要进行数据存储检查。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3967', '核心业务系统-车险', '严重错误', '2015-10-26', '商业险：PICS010011核心业务系统调用PICS发生异常！', '无', '转保业务的投保查询程序中riskcode属性的大小写拼写有误，导致数据匹配失败从而影响正常流程', '遗漏转保业务的测试', '对于投保查询接口调整的需求，增加转保业务的测试，以避免该类问题', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3970', '理赔工作流系统', '次要错误', '2015-11-03', '20151031临时升级:北京代位求偿历史案件补提结案节点报错-理赔事业部20150819100643R', '理赔事业部20150819100643R', '补传结案时间时该时间取值为空', '此需求改造了北京平台多个接口，着重关注了各个节点正常的交互。由于失败补提功能不是经常使用，因此仅抽取了查勘等节点进行补提，没有对所有接口进行补提，遗漏测试了结案补提功能。对于系统已经结案（案件已转储）但和平台交互失败或没交互的数据，结案补提报错。', '对于改造平台接口的需求，也要考虑各种失败数据的处理，要增加对各接口失败补提功能的测试，避免该类问题；', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3982', '新人身险打印系统', '严重错误', '2016-02-29', '打印保单格式内容偏向右边，打印不全', '意健险部20160205094253R_新疆分公司纸质保单打印调整', '因新疆保单打印需求调整了打印模板，导致非新疆机构打印时保单格式内容偏向右边，打印不全。', '该需求是对新疆地区保单打印做调整，影响到了其它地区，仅测试了新疆地区，未测试其它地区，导致漏检。', '要在测试中考虑常规地区和非常规地区，并把相关案例加入验证测试库，避免类似情况再次发生。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('3983', '呼叫中心服务系统', '严重错误', '2016-03-23', '报案转专岗处理后，无法选择出险时间重新抄单', '95590呼叫中心20160310093521R', '版本冲突', '‘转专岗’这种特殊的报案类型未覆盖到', '针对报案业务，要覆盖所有的报案类型。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('', '收付费系统', '严重错误', '2016-04-13', '验收发现数据只有e开头的计算书，生产上至少要有e、w和除此以外的险种数据', '财务管理部20160216141508R', NULL, '具体执行的时候，测试人员从收付费将数据提交到资金系统时，观察到原有的限制“还没有收到保费，不能进行该操作！”已经被放开，就视作用例成功通过了。实际上测试用例没有执行完整，没有检查在资金系统完成支付并返回收付费系统生成凭证的结果，故未发现该问题。', '不能仅从中间状态就判断用例是否成功，而是应该把整个交易彻底执行完成后检查返回信息的存储结果是否正确后才能判断是否真正成功。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
