INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('PM201712140323', '收付费系统', NULL, '2017-12-14', '航保中心的一部分保单和批单，在收付系统内“代签发缴费收据”内搜索提示报错：null，在“到款确认”、“领款确认”雨“批量到领款确认”内均找不到这些保单号 PCAA201731163101000037 PCAA201731163101000036 PCAA201731163101000038 PCAA201731163101000048 PCAA201731163101000039 PCAA201731163101000040 PCAA201731163101000047 PCAA201731163101000041 PCAA201731163101000042 PCAA201731163101000043 PCAA201731163101000046 PCAA201731163101000045 PCAA201731163101000044 PCAA201731163101000035 ECAA201731163101000044 ECAA201731163101000043 ECAA201731163101000049 ECAA201731163101000048 ECAA201731163101000047 ECAA201731163101000046', NULL, '由于开发时，考虑不全面，签发收据与代签发收据公用一个页面，少判断查询条件的NULL值', '未考虑到此功能对代签发缴费收据页面的影响', '在设计测试用例过程中，充分考虑新增功能点对其他同类型菜单的影响', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
INSERT INTO `tbl_report_monthly_undetected_summary`(`ITMS_CODE`, `SYSTEM_NAME`, `SERIOUS_LEVEL`, `REPORT_DATE`, `SUMMARY`, `REQUIREMENT_CODE`, `REASON_ANALYSIS`, `MISSED_REASON`, `EXPERIENCE_SUMMARY`, `CREATE_BY`, `CREATE_DATE`, `LAST_UPDATE_BY`, `LAST_UPDATE_DATE`, `STATUS`) VALUES ('PM201912026614', '销售管理系统', '次要错误', '2019-12-13', '操作步骤：个人代理协议复制--P51010701137-2019001      点击复制暂存—提示报错:必须输入保证人姓名，但是提示红框内的信息均无法录入。', NULL, '开发与测试需求时未能考虑周全，遗漏了对‘个人代理协议复制’页面相关信息的校验，导致历史数据在协议复制功能下不能跑通流程', '在测试用例中只包含了新数据的协议复制案例，未包含历史数据的协议复制案例', '设计测试用例时多考虑一些生产上的业务场景，尤其是特殊场景的数据及历史数据。', 1, '2020-11-04 10:21:45', 1, '2020-11-04 10:21:45', 1);
