mapper.select:
SELECT c.ID, c.SYSTEM_ID, c.CASE_CATALOG_ID, c.CASE_NAME, c.CASE_NUMBER, c.CASE_PRECONDITION, c.CASE_TYPE, c.ARCHIVE_STATUS, c. STATUS, c.CREATE_BY, c.CREATE_DATE, c.LAST_UPDATE_BY, c.LAST_UPDATE_DATE, s.SYSTEM_NAME systemName, u.USER_NAME userName, c.EXPECT_RESULT, c.INPUT_DATA, c.TEST_POINT, c.MODULE_NAME, c.BUSINESS_TYPE, c.CASE_DESCRIPTION FROM tbl_case_info c LEFT JOIN tbl_system_info s ON c.SYSTEM_ID = s.ID LEFT JOIN tbl_user_info u ON c.CREATE_BY = u.ID
and c.SYSTEM_ID = #{tblCaseInfo.systemId}
and c.CASE_CATALOG_ID = #{tblCaseInfo.caseCatalogId}
and c.CASE_NUMBER like concat("%",#{tblCaseInfo.caseNumber,jdbcType=VARCHAR},"%")
and c.CASE_NAME like concat("%",#{tblCaseInfo.caseName,jdbcType=VARCHAR},"%")
and c.CASE_TYPE = #{tblCaseInfo.caseType,jdbcType=TINYINT}
and c.ARCHIVE_STATUS = #{tblCaseInfo.archiveStatus,jdbcType=TINYINT}
and c.CREATE_BY in
#{uId,jdbcType=BIGINT}
and c.BUSINESS_TYPE like concat("%",#{filter.businessType,jdbcType=VARCHAR},"%")
and c.MODULE_NAME like concat("%",#{filter.moduleName,jdbcType=VARCHAR},"%")
and c.TEST_POINT like concat("%",#{filter.testPoint,jdbcType=VARCHAR},"%")
and c.CASE_DESCRIPTION like concat("%",#{filter.caseDescription,jdbcType=VARCHAR},"%")
and c.CASE_NUMBER like concat("%",#{filter.caseNumber,jdbcType=VARCHAR},"%")
and c.CASE_NAME like concat("%",#{filter.caseName,jdbcType=VARCHAR},"%")
and c.CASE_TYPE = #{filter.type,jdbcType=TINYINT}
and c.ARCHIVE_STATUS = #{filter.aStatus,jdbcType=TINYINT}
and c.SYSTEM_ID in
#{systemId,jdbcType=INTEGER}
and c.CREATE_BY in
#{userIds,jdbcType=BIGINT}
and c.STATUS = 1 order by c.ID desc
limit #{start},#{rows}

SELECT DISTINCT c.ID, c.SYSTEM_ID, c.CASE_CATALOG_ID, c.CASE_NAME, c.CASE_NUMBER, c.CASE_PRECONDITION, c.CASE_TYPE, c.ARCHIVE_STATUS, c. STATUS, c.CREATE_BY, c.CREATE_DATE, c.LAST_UPDATE_BY, c.LAST_UPDATE_DATE, s.SYSTEM_NAME systemName, u.USER_NAME userName, c.EXPECT_RESULT, c.INPUT_DATA, c.TEST_POINT, c.MODULE_NAME, c.BUSINESS_TYPE, c.CASE_DESCRIPTION FROM tbl_case_info c LEFT JOIN tbl_system_info s ON c.SYSTEM_ID = s.ID LEFT JOIN tbl_user_info u ON c.CREATE_BY = u.ID
LEFT JOIN tbl_project_system ps ON ps.SYSTEM_ID = s.ID AND ps.status = 1 AND ps.RELATION_TYPE = 1 LEFT JOIN tbl_project_group pgroup ON pgroup.PROJECT_ID = ps.PROJECT_ID AND pgroup.STATUS=1
pgroup.id IN( SELECT guser2.PROJECT_GROUP_ID FROM tbl_project_group_user guser2 WHERE guser2.USER_ID= #{uid} AND guser2.STATUS=1 )
and c.SYSTEM_ID = #{tblCaseInfo.systemId}
and c.CASE_CATALOG_ID = #{tblCaseInfo.caseCatalogId}
and CASE_NUMBER like concat("%",#{tblCaseInfo.caseNumber,jdbcType=VARCHAR},"%")
and CASE_NAME like concat("%",#{tblCaseInfo.caseName,jdbcType=VARCHAR},"%")
and CASE_TYPE = #{tblCaseInfo.caseType,jdbcType=TINYINT}
and c.SYSTEM_ID in
#{systemId,jdbcType=INTEGER}
and ARCHIVE_STATUS = #{tblCaseInfo.archiveStatus,jdbcType=TINYINT}
and c.CREATE_BY in
#{uId,jdbcType=BIGINT}
and c.BUSINESS_TYPE like concat("%",#{filter.businessType,jdbcType=VARCHAR},"%")
and c.MODULE_NAME like concat("%",#{filter.moduleName,jdbcType=VARCHAR},"%")
and c.TEST_POINT like concat("%",#{filter.testPoint,jdbcType=VARCHAR},"%")
and c.CASE_DESCRIPTION like concat("%",#{filter.caseDescription,jdbcType=VARCHAR},"%")
and CASE_NUMBER like concat("%",#{filter.caseNumber,jdbcType=VARCHAR},"%")
and CASE_NAME like concat("%",#{filter.caseName,jdbcType=VARCHAR},"%")
and CASE_TYPE = #{filter.type,jdbcType=TINYINT}
and ARCHIVE_STATUS = #{filter.aStatus,jdbcType=TINYINT}
and c.SYSTEM_ID in
#{systemId,jdbcType=INTEGER}
and c.CREATE_BY in
#{userIds,jdbcType=BIGINT}
and c.STATUS = 1 order by c.ID desc
limit #{start},#{rows}

